site.headStyle:
body, html {
  scroll-behavior: auto;
  height: 100%;
  overflow-x: hidden;
  overflow-y: auto;
  width: 100%;
}


.sticky {
  position: fixed;
  top: 0;
  width: 100%
}

.hero {
    min-height: 90vh;
}
.heading1 {
  text-shadow: 2px -2px 5px rgba(0, 0, 0, 0.75);
}

.button.page-link-active {
  background-color: #FC6F5C;
}

.my-colorbutton{
    background-color: #dadee2;
}
.my-colorbutton:hover{
    background-color: #e7e1a4;
}

.hover-effect-1 .text-element{
    opacity: .001;
    filter:alpha(opacity=1); /* For IE8 and earlier */
}
.hover-effect-1:hover .text-element{
    opacity: 1;
    filter:alpha(opacity=100); /* For IE8 and earlier */
}


.zoom {
  transition: transform .2s; /* Animation */
  width: 200px;
  height: 200px;
  margin: 0 auto;
}

.zoom:hover {
  transform: scale(1.2); /* (120% zoom - Note: if the zoom is too large, it will go outside of the viewport) */
}
